mapper.select:
SELECT count(a.id) warningNumber from ck_early_warning a
1=1 and a.del_flag = '0'
and a.school_id=#{map.schoolId}

SELECT count(a.id) alarmNumber from ck_early_alarm a
1=1 and a.del_flag = '0'
and a.school_id=#{map.schoolId}

select smf.id as id, smf.sup_name as name, smf.license_number from sup_main_filing smf
smf.school_id=#{map.schoolId}
and smf.del_flag='0'

select count(smf.id) as Total from sup_main_filing smf
smf.school_id=#{map.schoolId}
and smf.del_flag='0'

select ifnull(a.user_id,0)as userId, ifnull(a.real_name,'无') AS realName, ifnull(b.label,'无')AS label, ifnull(a.health_number,0) as healthNumber from sys_user a left join (select * from sys_dict where del_flag='0' and type='position')b on b.value = a.position a.del_flag = '0' and a.user_type = '2'
and a.union_id = #{map.schoolId}
and a.position = '2'

select ifnull(a.user_id,0)as userId, ifnull(a.real_name,'无') AS realName, ifnull(b.label,'无')AS label, ifnull(a.health_number,0) as healthNumber from sys_user a left join (select * from sys_dict where del_flag='0' and type='position')b on b.value = a.position a.del_flag = '0' and a.user_type = '2'
and a.union_id = #{map.schoolId}
and a.position = '3'

SELECT count( a.user_id ) AS 'total', ifnull( CONVERT ( sum( a.ischen ) / count( a.user_id ), DECIMAL ( 15, 2 ) ), 0 ) AS 'eqRatio' FROM ( SELECT a.user_id, CASE a.morning_check WHEN '1' THEN 1 ELSE 0 END AS 'ischen' FROM sys_user a
1 = 1 AND a.del_flag = '0'
AND a.union_id = #{map.schoolId}
) a

SELECT b.label,a.brand, a.start_date, a.use_year, a.restaurant_name from vd_device a LEFT join(SELECT * from sys_dict sd sd.del_flag='0'
) b on b.id=a.device_type
and a.school_id=#{map.schoolId}
and a.del_flag='0'

select count(a.id)as 'total',ifnull(convert(sum(a.isPassed)/count(a.id),DECIMAL(15,2)),0)as 'eqRatio', ifnull( CONVERT ( sum( a.isWell ) / count( a.id ), DECIMAL ( 15, 2 ) ), 0 ) AS 'eqWell' from ( select a.id, case a.is_passed when '1' then 1 else 0 end as 'isPassed', CASE a.run_well WHEN '1' THEN 1 ELSE 0 END AS 'isWell' from vd_device a
1=1 and a.del_flag='0'
and a.school_id = #{map.schoolId}
)a

SELECT vsi.food_id, vsi.foodName, b.pur_time as time, b.qualified FROM view_sup_ingredients_filing vsi RIGHT join ( SELECT a.pur_time, CASE WHEN ppf.is_qualified='1' THEN '合格' ELSE '不合格' END as qualified, ppf.filing_id
FROM pur_purchase_filing ppf LEFT JOIN ( SELECT * FROM pur_purchase_main ppm WHERE ppm.del_flag = 0
and ppm.school_id = #{map.schoolId}
) a ON a.id = ppf.pur_id
ppf.is_del = 0
AND a.pur_time BETWEEN #{map.startingTime} AND #{map.endTime}
) b ON b.filing_id = vsi.food_id

select
(select ifnull(sum(a.isSample),0)as 'sampleRecord' from (
select a.school_id as schoolId,c.sch_name as schoolName,a.rd_date as rdDate,
CASE WHEN max(b.sr_type) ='0' THEN 0
WHEN (min(b.sr_type) ='0' AND max(b.sr_type) = '1') THEN 1
ELSE 1 END AS isSample,
CASE WHEN max(b.sr_type) ='0' THEN '未留样'
WHEN (min(b.sr_type) ='0' AND max(b.sr_type) = '1') THEN '部分留样'
ELSE '全部留样'
END AS isSampleZ
from re_daily a
left join re_daily_source b on b.daily_id = a.id
left join se_school c on c.id = a.school_id

  1=1 and a.del_flag = '0'

    AND a.school_id = #{map.schoolId}


group by a.rd_date
order by a.rd_date desc
) a )as'sampleRecord',


(select count(*) from sl_dishwash_note

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'dishwashNote',


(select count(*) from sl_accompany_dinner

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'accompanyDinner',
(select count(*) from sl_food_additive

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'equipmentWash',


(select count(*) from sl_food_additive

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'foodAdditive',
(select count(*) from sl_diningarea_wash

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'diningareaWash',

(select count(*) from sl_kitwaste_treatment

  1=1 and del_flag = '0'

    AND school_id = #{map.schoolId}


) as 'kitwasteTreatment'

select CASE
WHEN early_warning IN ( 1 ) THEN '营业执照' WHEN early_warning IN ( 2 ) THEN '许可证' WHEN early_warning = '3' THEN a.human WHEN early_warning = '4' THEN date_format( a.purchase_time, '%Y-%m-%d' ) ELSE '' END AS tital, CASE
WHEN early_warning IN ( '1', '2' ) THEN '证照预警' WHEN early_warning IN ( 3 ) THEN '人员预警' WHEN early_warning IN ( '4' ) THEN '食材预警' ELSE '' END AS type, d.description AS title, a.early_time AS time, a.supplier_name, b.sch_name AS schoolName, b.id AS schoolId from ck_early_warning a LEFT JOIN ( SELECT a.* FROM se_school a WHERE 1 = 1 AND a.del_flag = '0' ) b ON b.id = a.school_id LEFT JOIN ( SELECT * FROM sys_dict a WHERE 1 = 1 AND a.del_flag = '0' AND a.type = 'early_warning' ORDER BY a.sort ) d ON d. VALUE = a.early_warning
1=1 and a.del_flag = '0'
and DATE_FORMAT(a.early_time,'%Y') = #{map.year}
AND b.id = #{map.schoolId}
